import React, { useState } from 'react';

export default function Textform() {
  const [text, setText] = useState('Enter Text Here');
  const [bk, setBk] = useState({
    color: 'black',
    backgroundColor: 'white'
  });
  
  const handleConvertToUpperCase = () => {
    setText((prevText) => prevText.toUpperCase());
  };
  const handleConvertTolowerCase = () => {
    setText((haha) => haha.toLowerCase());
  };
  const removeSpaces = () => {
    setText((prevText) => prevText.replace(/\s/g, '')); 
  };
  const cleartext = () => {
    setText(""); 
  };
  const Copytext = () => {
    
    navigator.clipboard.writeText(text)
      .then(() => {
        alert('Text copied to clipboard:', text);
      })
      .catch((error) => {
        console.error('Error copying text to clipboard:', error);
      });
  };
  
  const Downloadtext = () => {
    // Create a Blob with the text content
    const blob = new Blob([text], { type: 'text/plain' });
  
    // Create a temporary URL for the Blob
    const url = URL.createObjectURL(blob);
  
    // Create a link element
    const link = document.createElement('a');
  
    // Set the link's attributes
    link.href = url;
    link.download = 'Text.txt';
  
    // Append the link to the document
    document.body.appendChild(link);
  
    // Trigger a click on the link to start the download
    link.click();
  
    // Remove the link from the document
    document.body.removeChild(link);
    
    // Revoke the temporary URL
    URL.revokeObjectURL(url);
  };
  

  const handleChange = (e) => {
    setText(e.target.value);
  };
  const darkmode= () => {
   setBk((haha)=>haha.backgroundColor='dark')
  };
   

  return (
    <div>
      <form className='container mt-3'>
        <div className="form-group">
          <label className='pb-2' htmlFor="mybox"><h1>Enter Text Below</h1></label>
          <div className="form-check form-switch">
  <input onClick={darkmode} className="form-check-input" type="checkbox" id="radio" />
</div>
<textarea style={bk} className="form-control" id="mybox"  rows="10" value={text} onChange={handleChange}></textarea>
<button  type="button"className="btn m-2 btn-primary" onClick={handleConvertToUpperCase} > Convert To Upper Case </button>
<button  type="button"className="btn m-2 btn-primary"  onClick={handleConvertTolowerCase} > Convert To lower Case </button>
<button  type="button"className="btn m-2 btn-primary"  onClick={removeSpaces} > Remove Spaces </button>
<button  type="button"className="btn m-2 btn-primary"  onClick={cleartext} > Cleartext</button>
<button  type="button"className="btn m-2 btn-primary"  onClick={Downloadtext} > Downloadtext</button>
<button  type="button"className="btn m-2 btn-primary"  onClick={Copytext} > Copytext</button>
<h4>Number of character: {text.length} Number of Words: {text.length>0?text.split(" ").length:0}</h4>
        </div>
      </form>
    </div>
  );
}
