import React, { useState } from 'react';
import Textform from './Components/Textform';
import Navbar from './Components/Navbar';
import Alert from './Components/Alert';
import Home from './Components/Home';
import {
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  RouterProvider,
} from "react-router-dom";

function App() {
  const [mode, setMode] = useState('light');
  const [showAlert, setShowAlert] = useState(false);

  const toggleMode = () => {
    setShowAlert(true);

    if (mode === 'light') {
      setMode('dark');
      document.body.style.backgroundColor = '#0E505F '; // Change to the desired dark background color
    } else {
      setMode('light');
      document.body.style.backgroundColor = 'white'; // Change to the desired light background color
    }

    // Hide the alert after 1 second
    setTimeout(() => {
      setShowAlert(false);
    }, 1000);

    console.log(mode);
  };

  return (
    <>
     <Navbar title="Abdul Rafay" mode={mode} toggle={toggleMode} Search="NotNow" />
     {showAlert && <Alert mode={mode} />} 
    <Route path="Home" element={<Home mode={mode}/>}>
      <Route path="Hero" element={<Textform mode={mode} />} />
    </Route>



    </>
  );
}

export default App;
