import React, { useState } from 'react';

export default function Textform() {
  const [text, setText] = useState('Enter Text Here');

  const handleConvertToUpperCase = () => {
    setText((prevText) => prevText.toUpperCase());
  };
  const handleConvertTolowerCase = () => {
    setText((haha) => haha.toLowerCase());
  };
  const removeSpaces = () => {
    setText((prevText) => prevText.replace(/\s/g, '')); 
  };
  const cleartext = () => {
    setText(""); 
  };

  const handleChange = (e) => {
    setText(e.target.value);
  };

  return (
    <div>
      <form className='container mt-3'>
        <div className="form-group">
          <label className='pb-2' htmlFor="mybox"><h1>Enter Text Below</h1></label>
<textarea  className="form-control" id="mybox"  rows="10" value={text} onChange={handleChange}></textarea>
<button  type="button"className="btn m-2 btn-primary" onClick={handleConvertToUpperCase} > Convert To Upper Case </button>
<button  type="button"className="btn m-2 btn-primary"  onClick={handleConvertTolowerCase} > Convert To lower Case </button>
<button  type="button"className="btn m-2 btn-primary"  onClick={removeSpaces} > Remove Spaces </button>
<button  type="button"className="btn m-2 btn-primary"  onClick={cleartext} > Cleartext</button>
